mapper.update:
UPDATE	BGROUP
SET		BG_NOW_CNT = BG_NOW_CNT-1
WHERE	BG_NUM = ${bg_num}

mapper.select:
SELECT BG_NUM, BG_NAME, BG_START_DTTM, BG_END_DTTM, BG_NOW_CNT, BG_END_CNT, BG_LOCATION, BG_XY, SPORTS2_CD, get_code_name(SKILL_CD) SKILL_CD, AGE_RANGE, get_code_name(GENDER_CD) GENDER_CD, BG_TEAM_CNT, DIS FROM ( SELECT ROWNUM R, A.* FROM ( SELECT B.*, CALC_DISTANCE( SUBSTR(BG_XY, 0, INSTR(BG_XY, ',', 1, 1)-1), SUBSTR(BG_XY, INSTR(BG_XY, ',', 1, 1)+2, LENGTH(BG_XY)), SUBSTR(#{m_xy}, 0, INSTR(#{m_xy}, ',', 1, 1)-1), SUBSTR(#{m_xy}, INSTR(#{m_xy}, ',', 1, 1)+2, LENGTH(#{m_xy})) ) DIS FROM BGROUP B WHERE BG_END_DTTM > SYSDATE
and SPORTS2_CD like '%'||#{keyval}||'%'
ORDER BY DIS, BG_END_DTTM, BG_NUM ) A
AND LPAD(round(DIS, 3), 8, '0') || to_char(BG_END_DTTM, 'yyyy/mm/dd hh24:mi') || LPAD(BG_NUM, 6, '0') > LPAD(round(#{end_dis}, 3), 8, '0') || to_char(#{end_dttm}, 'yyyy/mm/dd hh24:mi') || LPAD(#{end_num}, 6, '0')
) WHERE R <= #{scroll_rec}

SELECT BG_NUM, BG_NAME, BG_START_DTTM, BG_END_DTTM, BG_NOW_CNT, BG_END_CNT, BG_LOCATION, BG_XY, SPORTS2_CD, get_code_name(SKILL_CD) SKILL_CD, AGE_RANGE, get_code_name(GENDER_CD) GENDER_CD, BG_TEAM_CNT, DIS FROM ( SELECT ROWNUM R, A.* FROM ( SELECT B.*, CALC_DISTANCE( SUBSTR(BG_XY, 0, INSTR(BG_XY, ',', 1, 1)-1), SUBSTR(BG_XY, INSTR(BG_XY, ',', 1, 1)+2, LENGTH(BG_XY)), SUBSTR(#{m_xy}, 0, INSTR(#{m_xy}, ',', 1, 1)-1), SUBSTR(#{m_xy}, INSTR(#{m_xy}, ',', 1, 1)+2, LENGTH(#{m_xy})) ) DIS FROM BGROUP B WHERE BG_END_DTTM <= SYSDATE
and SPORTS2_CD like '%'||#{keyval}||'%'
ORDER BY DIS, BG_END_DTTM, BG_NUM ) A
AND LPAD(round(DIS, 3), 8, '0') || to_char(BG_END_DTTM, 'yyyy/mm/dd hh24:mi') || LPAD(BG_NUM, 6, '0') > LPAD(round(#{end_dis}, 3), 8, '0') || to_char(#{end_dttm}, 'yyyy/mm/dd hh24:mi') || LPAD(#{end_num}, 6, '0')
) WHERE R <= #{scroll_rec}
